mapper.select:
SELECT BCT.BCT_CODE, BCT.BCT_NAME, SVC.SVC_PRI FROM BCT
INNER JOIN SVC ON BCT.BCT_CODE = SVC.BCT_CODE WHERE BUS_NO = #{no}
ORDER BY SVC_PRI

SELECT
MENU.MENU_NAME FROM MENU INNER JOIN BKM ON MENU.MENU_NO = BKM.MENU_NO
INNER JOIN BK ON BKM.BK_NO = BK.BK_NO WHERE BK.BK_NO = #{bk_no}

mapper.update:
UPDATE BUS SET
BUS_PHONE = #{bus_phone}, BUS_POST = #{bus_post}, BUS_ADDR =
#{bus_addr}, BUS_ADDR2 = #{bus_addr2} WHERE BUS_NO = #{bus_no}

UPDATE GLR SET
GLR_FILE = #{glr_file}, GLR_LOC = #{glr_loc} WHERE BUS_NO = #{bus_no}
AND GCT_NO = 2

mapper.select:
SELECT COUNT(*) FROM
BK JOIN PER ON BK.PER_NO = PER.PER_NO WHERE BUS_NO = #{no} AND
PER_NAME = #{per_name}
